var BananaFrame = function() {
    var c = $("#frameBox .iconBox .item"),
        a = $("#imgBox > .view > .stickerBox"),
        // e = "tl t tr l r bl b br".split(" "),
        e = ["all"],
        f = function() {
            c.removeClass("active");
            $("img.frame", a).remove()
        };
    return {
        init: function() {
            c.on("click", function() {
                if ($(this).hasClass("active")) f();
                else {
                    var c = $(this).attr("data-frame");
                    f();
                    $(this).addClass("active");
                    for (var d = 0; d < e.length; d++) {
                        var b = $("<img>");
                        b.addClass("frame");
                        b.addClass(e[d]);
                        b.attr("src", "/media/banana_camera/img/frame/" + c + "/" + e[d] + ".png");
                        a.append(b);
                        b.on("dragstart",
                            function() {
                                return !1
                            })
                    }
                    $("img.frame.all", a).width(a.width());
                    $("img.frame.all", a).height(a.height());
                    $("img.frame.t, img.frame.b", a).width(a.width() - 160);
                    $("img.frame.l, img.frame.r", a).height(a.height() - 160)
                }
            })
        },
        clearFrame: f
    }
}();